import './App.css';

import React, { useEffect, useState } from 'react';
import { predict } from "./components/API";


function useKeyPress() {
  let timer;
  let interval = 1000;

  const [keyPressed, setKeyPressed] = useState(true);

  function keyup() {
    setKeyPressed(false);
    clearTimeout(timer);
  }

  useEffect(() => {
