import './App.css';

import React, { useEffect, useState } from 'react';
import { predict } from "./components/API";

function App() {

  function UsePrediction(input) {
    const [prediction, setPrediction] = useState('yeet');

    setPrediction(predict(input.value))
  }

  function useKeyPress() {
    let timer;
    let interval = 1000;
    const [keyPressed, setKeyPressed] = useState(true);

    function keyup() {
      setKeyPressed(false);
      clearTimeout(timer);
      let input = document.getElementById('input');
      if (input.value) {
        timer = setTimeout(UsePrediction(input.value), interval);
      }
      setKeyPressed(true);
    }

    useEffect(() => {
      window.addEventListener('keyup', keyup);
      return () => {
        window.removeEventListener('keyup', keyup);
      };
    }, []);

    return keyPressed;
  }

  return (
    <div>
      <nav>
        <a href="/om">Om siden</a>
      </nav>
      <main>
        {useKeyPress()}
        <h1>ğŸ¤µ Mann eller Kvinne? ğŸ’ƒ</h1>
        <p>Skriv og la maskinen gjette om du er mann eller kvinne ğŸ¤–</p>
        <textarea id='input'></textarea>
        <div className="prediction">
          <p>Maskinen gjetter:</p>
          <p>{prediction}</p>
        </div>
      </main>
      <footer>
        <p>Laget av <a href="https://lblend.moe">LBlend</a></p>
        <a href="https://github.com/LBlend/mann-eller-kvinne">Kildekode</a>
      </footer>
    </div>
  );
}

export default App;
